record.field:
no_days = []
counter = 0
for w in ['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday']:
    if env['ir.config_parameter'].sudo().get_param('postpone_email_'+w):
        no_days.append(counter)
    counter += 1
no_days_count = len(no_days)

# If all days were set, this would cause an impossible situation, so
# if all days are set, we don't run the code
if no_days_count and no_days_count < 7:
    for rec in records:
        if rec.state == 'in_queue':
            date_today = datetime.datetime.today()
            today_day = date_today.weekday()
            next_try_day = today_day

            # If the mailing is to be sent on a no_day, we try to find the next yes date
            while next_try_day in no_days:
                if next_try_day == 6:
                    next_try_day = 0
                else:
                    next_try_day += 1

            days_ahead = 0
            if next_try_day > today_day:
                days_ahead = next_try_day - today_day
            if next_try_day < today_day:
                days_ahead = 7 - (today_day - next_try_day)

            log("TODAY: %s | NEXT: %s | DAYS: %s" % (today_day, next_try_day, days_ahead))

            if next_try_day != today_day:
              rec.write({'schedule_date': date_today + datetime.timedelta(days=days_ahead)})
