record.field:
if record.team_id:
    for tm in record.team_id.member_ids:
        if tm.mobile:
            base_url = request.env['ir.config_parameter'].get_param('web.base.url')
            base_url += '/web#id=%d&view_type=form&model=%s' % (record.id, record._name)
            env['sms.sms'].create({
                                    'number': tm.mobile,
                                    'body': 'New lead! %s' % base_url
                                    })
